import React, { useEffect } from 'react'
import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { listProducts } from '../actions/productActions';

const ItemMenu = () => {
    const dispatch=useDispatch()
//   const [product, setProduct] = useState([]);
    const [item, setItem] = useState("accessories");
    console.log(item)

    useEffect(() => {
        dispatch(listProducts)
        
    }, [item])
   
    const productList = useSelector((state) => state.productList)
    const { loading, error, products, page, pages } = productList

    const product = products.filter(it => it.category === item)
    return (
      <>
        
          <div className="menu">
            <div
              className={
                item === "accessories" ? "active nav__link" : "nav__link"
              }
              onClick={() => setItem("accessories")}
            >
              <p>Accessories</p>
            </div>

            <div
              className={item === "laptop" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("laptop")}
            >
              <p>Laptop</p>
            </div>
            <div
              className={item === "phone" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("phone")}
            >
              <p>Phone</p>
                    </div>
                    <div
              className={item === "tab" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("tab")}
            >
              <p>Tab</p>
            </div>
            </div>
            <div>
                <Product product={product}/>
            </div>
       
      </>
    );
}

export default ItemMenu
