import React from 'react'
// import axios from "axios"
import { Row, Col, Carousel } from 'react-bootstrap'

import Product from '../components/Product'
import { useState } from 'react'
import { useEffect } from 'react'
import {useDispatch,useSelector} from "react-redux"
import {listProducts} from "../actions/productActions"
import Loader from '../components/Loader'
import Message from '../components/Message'
import Image1 from "../images/banner-ecom1.jpg";
import Image2 from "../images/banner-ecom.jpg";
import Image3 from "../images/banner-ecom3.jpg";
import Image4 from "../images/banner-ecom4.jpg";


const HomeScreen = () => {
    const dispatch=useDispatch()
    const productList=useSelector(state=>state.productList)
    const {loading,error,products}=productList
    // const [products,setProducts]=useState([])

    useEffect(()=>{
        // const fetchProducts=async()=>{
        //     const { data } = await axios.get("/api/products/");
        //     setProducts(data)
        // }
        // fetchProducts()
        dispatch(listProducts())

    },[dispatch])

    // const products=[]
    return (
      <div className="homescreen">
      <h1 className="text-center">Latest Products</h1>

      {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Row>
            {products.map((pd) => (
              <Col key={pd._id} sm={12} md={6} lg={4} xl={3}>
                <Product product={pd} />
              </Col>
            ))}
          </Row>
        )}
      </div>
    );
}

export default HomeScreen
