import React, { useEffect } from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../actions/productActions';
import Product from './Product'

const ItemMenu = () => {
    const dispatch=useDispatch()
    // const [product, setProduct] = useState([]);
    const [item, setItem] = useState("accessories");
    console.log(item)

    useEffect(() => {
        dispatch(listProducts)
        
    }, [item])
    
   
    const productList = useSelector((state) => state.productList)
    const { loading, error, products, page, pages } = productList
    let product;
    if (products) {
         product = products.filter(it => it.category.toLowerCase() === item)
    }

   
    return (
      <div div className="item-menu">
        
          <div className="menu">
            <div
              className={
                item === "accessories" ? "active nav__link" : "nav__link"
              }
              onClick={() => setItem("accessories")}
            >
              <p>Accessories</p>
            </div>

            <div
              className={item === "laptop" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("laptop")}
            >
              <p>Laptop</p>
            </div>
            <div
              className={item === "phone" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("phone")}
            >
              <p>Phone</p>
                    </div>
                    <div
              className={item === "tab" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("tab")}
            >
              <p>Tab</p>
            </div>
            </div>
            <div className="selected-item-product">
                {product.map(pd => <Product product={product} key={product._id}/>)}
            </div>
       
      </div>
    );
}

export default ItemMenu
