import React, { useEffect } from 'react'
import { useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../actions/productActions';
import Product from './Product'

const ItemMenu = () => {
    const dispatch=useDispatch()
    // const [product, setProduct] = useState([]);
    const [item, setItem] = useState("accessories");
    console.log(item)

    const productList = useSelector((state) => state.productList)
    const { products} = productList
  
   useEffect(() => {
    dispatch(listProducts)
    
   }, [item])
    const product = products.filter(it => it.category.toLowerCase() === item)
    console.log(product)
   
  return (
    <>
      <div className="item-menu-title">
        <div>
        <h1>New Product</h1>
        </div>
      <div style={{width:"340px"}}>  <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the indus.</p></div>
      </div>
      <div className="item-menu">
        
          <div className="menu" style={{marginBottom:"90px"}}>
            <div
              className={
                item === "accessories" ? "active nav__link" : "nav__link"
              }
              onClick={() => setItem("accessories")}
            >
              <p>Accessories</p>
            </div>

            <div
              className={item === "laptop" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("laptop")}
            >
              <p>Laptop</p>
            </div>
            <div
              className={item === "phone" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("phone")}
            >
              <p>Phone</p>
                    </div>
                    <div
              className={item === "tab" ? "active nav__link" : "nav__link"}
              onClick={() => setItem("tab")}
            >
              <p>Tab</p>
            </div>
            </div>
            
            <Row>
            {product.map((pd) => (
              <Col key={pd._id} sm={12} md={6} lg={4} xl={3}>
                <Product product={pd} />
              </Col>
            ))}
          </Row>
             
      </div>
      </>
       
     
    );
}

export default ItemMenu
